// src/Login.js
import React, { useState } from "react";

const Login = () => {
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("User"); // Default role is 'User'
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();

    // Perform your authentication and role-based logic here (e.g., call an API)

    if (fullName === "" || email === "" || role === "") {
      setError("All fields are required.");
    } else {
      // Simulate login and role-based behavior (for now, it's just a basic check)
      alert(`Logged in as ${fullName} with role: ${role}`);
      // Redirect to another page or perform your role-based logic here
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-black">
      <div className="bg-gray-800 p-8 rounded-lg shadow-lg w-96">
        <h2 className="text-2xl font-bold text-white text-center mb-4">
          Login
        </h2>

        {error && <div className="text-red-500 text-sm mb-4">{error}</div>}

        <form onSubmit={handleLogin}>
          {/* Full Name Field */}
          <div className="mb-4">
            <label
              htmlFor="fullName"
              className="block text-sm font-medium text-white"
            >
              Full Name
            </label>
            <input
              type="text"
              id="fullName"
              className="w-full p-2 mt-2 border border-gray-600 bg-gray-700 text-white rounded-md focus:ring-purple-500 focus:border-purple-500"
              value={fullName}
              onChange={(e) => setFullName(e.target.value)}
              required
            />
          </div>

          {/* Email Field */}
          <div className="mb-4">
            <label
              htmlFor="email"
              className="block text-sm font-medium text-white"
            >
              Email
            </label>
            <input
              type="email"
              id="email"
              className="w-full p-2 mt-2 border border-gray-600 bg-gray-700 text-white rounded-md focus:ring-purple-500 focus:border-purple-500"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          {/* Role Field (Dropdown) */}
          <div className="mb-6">
            <label
              htmlFor="role"
              className="block text-sm font-medium text-white"
            >
              Role
            </label>
            <select
              id="role"
              className="w-full p-2 mt-2 border border-gray-600 bg-gray-700 text-white rounded-md focus:ring-purple-500 focus:border-purple-500"
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="Admin">Admin</option>
              <option value="User">User</option>
              <option value="Moderator">Moderator</option>
            </select>
          </div>

          <button
            type="submit"
            className="w-full bg-purple-600 text-white py-2 rounded-md hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-purple-500"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
