import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { supabase } from "../supabaseClient";
import bcrypt from "bcryptjs"; // Import bcrypt to hash and compare passwords

function Login() {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    role: "", // No default role value
  });
  const [error, setError] = useState(""); // State for error messages
  const [redirectMessage, setRedirectMessage] = useState(""); // State for redirect message
  const navigate = useNavigate(); // Initialize navigate function

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Reset error message

    try {
      // Check if the email exists in the 'users' table
      const { data, error: queryError } = await supabase
        .from("users")
        .select("id, password") // Only fetch id and password for verification
        .eq("email", formData.email)
        .single(); // .single() fetches one row, returns null if no match

      if (queryError) {
        throw queryError; // If there's an error with the query, handle it
      }

      if (data) {
        // If user exists, verify the password using bcrypt
        const isPasswordValid = await bcrypt.compare(formData.password, data.password);

        if (isPasswordValid) {
          console.log("Login Successful");
          setRedirectMessage("Redirecting to Home...");
          setTimeout(() => {
            // Pass the user id to the Home component via navigate state
            navigate("/Home", { state: { userId: data.id } });
          }, 2000); // Redirect after 2 seconds
        } else {
          setError("Incorrect password");
        }
      } else {
        setError("User not found. Please register first.");
      }
    } catch (error) {
      console.error("Error during login:", error);
      setError("An error occurred during login.");
    }
  };

  return (
    <div className="min-h-screen bg-black text-white flex justify-center items-center">
      <div className="w-full max-w-md">
        <h1 className="text-4xl font-bold mb-6 text-center">Login</h1>
        {error && <p className="text-red-500 text-center mb-4">{error}</p>}
        {redirectMessage && (
          <p className="text-green-500 text-center mb-4">{redirectMessage}</p>
        )}
        <form
          onSubmit={handleSubmit}
          className="bg-purple-900 p-6 rounded-lg shadow-lg"
          autoComplete="off"
        >
          <div className="mb-4">
            <label htmlFor="email" className="block text-lg font-medium mb-2">
              Email
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full p-3 rounded-lg bg-purple-800 text-white border border-purple-700"
              required
              autoComplete="new-email"
            />
          </div>

          <div className="mb-4">
            <label htmlFor="password" className="block text-lg font-medium mb-2">
              Password
            </label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="w-full p-3 rounded-lg bg-purple-800 text-white border border-purple-700"
              required
              autoComplete="new-password"
            />
          </div>

          <div className="mb-6">
            <label htmlFor="role" className="block text-lg font-medium mb-2">
              Role
            </label>
            <select
              id="role"
              name="role"
              value={formData.role}
              onChange={handleChange}
              className="w-full p-3 rounded-lg bg-purple-800 text-white border border-purple-700"
            >
              <option value="">Select a role</option>
              <option value="User">User</option>
              <option value="Admin">Admin</option>
              <option value="Moderator">Moderator</option>
            </select>
          </div>

          <button
            type="submit"
            className="w-full p-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700"
          >
            Login
          </button>
        </form>
        <div className="mt-4 text-center">
          <p>
            Don't have an account?{" "}
            <a href="/" className="text-purple-400">
              Register
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
