import React, { useState } from "react";
import { supabase } from "../supabaseClient"; // Ensure to import your Supabase client

function Login() {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    role: "User", // Default role
  });
  const [error, setError] = useState(""); // State for error messages

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Reset error message

    try {
      // Check if the email exists in the 'users' table
      const { data, error: queryError } = await supabase
        .from("users")
        .select("*")
        .eq("email", formData.email)
        .single(); // .single() fetches one row, returns null if no match

      if (queryError) {
        throw queryError; // If there's an error with the query, handle it
      }

      if (data) {
        // If user exists, verify the password
        if (data.password === formData.password) {
          console.log("Login Successful");
          alert("Login Successful!");
          setFormData({ email: "", password: "", role: "User" }); // Clear form data after login
        } else {
          setError("Incorrect password");
        }
      } else {
        setError("User not found. Please register first.");
      }
    } catch (error) {
      console.error("Error during login:", error);
      setError("An error occurred during login.");
    }
  };

  return (
    <div className="min-h-screen bg-black text-white flex justify-center items-center">
      <div className="w-full max-w-md">
        <h1 className="text-4xl font-bold mb-6 text-center">Login</h1>

        {error && <p className="text-red-500 text-center mb-4">{error}</p>} {/* Display error message */}

        <form
          onSubmit={handleSubmit}
          className="bg-purple-900 p-6 rounded-lg shadow-lg"
        >
          <div className="mb-4">
            <label htmlFor="email" className="block text-lg font-medium mb-2">
              Email
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full p-3 rounded-lg bg-purple-800 text-white border border-purple-700"
              required
            />
          </div>

          <div className="mb-4">
            <label
              htmlFor="password"
              className="block text-lg font-medium mb-2"
            >
              Password
            </label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="w-full p-3 rounded-lg bg-purple-800 text-white border border-purple-700"
              required
            />
          </div>

          <div className="mb-6">
            <label htmlFor="role" className="block text-lg font-medium mb-2">
              Role
            </label>
            <select
              id="role"
              name="role"
              value={formData.role}
              onChange={handleChange}
              className="w-full p-3 rounded-lg bg-purple-800 text-white border border-purple-700"
            >
              <option value="User">User</option>
              <option value="Admin">Admin</option>
              <option value="Moderator">Moderator</option>
            </select>
          </div>

          <button
            type="submit"
            className="w-full p-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700"
          >
            Login
          </button>
        </form>

        <div className="mt-4 text-center">
          <p>
            Don't have an account?{" "}
            <a href="/" className="text-purple-400">
              Register
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
